import React, {Component} from 'react';
import PropTypes from 'prop-types';
import Tab from "./Tab"
import AddTable from "./AddTable"
import RenderTables from "./RenderTables"

class Main extends Component {
    state = {
        chartType: 'tables',
        numExistingTables: 0,
        tables: [],
    }

    addATable = () => {
        this.setState({
            chartType: 'addNew',
        });
    }

    checkTable = (info) => {

        this.setState({
           chartType: 'tableDetail',
           tables: [{
               tableId: info.id,
               table: [{
                   //read data from database
               }],
           }]
        });
    }

    handleTableInfo = info => {
        if (info.isCancel) {
            this.setState({
                chartType: 'tables',
            });
        } else {
            // console.log(info);

            this.setState({
                chartType: 'tables',
                numExistingTables: 0,
                tables: this.state.tables.push({
                    capacity: info.capacity,
                    length: info.length,
                    width: info.width,
                }, )
            });
            console.log(this.state.tables);
        }
    }

    render() {
        let theView;
        if (this.state.chartType === 'tables') {
            theView = <RenderTables onCheckTableInfo={this.checkTable}
                        tablesToRender={this.state.chartType}/>;
        } else if (this.state.chartType === 'addNew') {
            theView =  <AddTable onSelectTableInfo={this.handleTableInfo}/>;
        } else if (this.state.chartType === 'tableDetail') {
            theView =  <div></div>;
        }
        return (
            <div className={"main-container"}>

                <div className={"tabs"}>
                    <p className={"tab"}>Dine in</p>
                </div>

                <div>
                    <button onClick={this.addATable}
                            className={"button"}>
                        Add Table
                    </button>
                </div>

                {theView}

            </div>
        );

    }
}

export default Main;
