import React, {PureComponent} from 'react';
import PropTypes from 'prop-types';

class TableChart extends PureComponent {

    handleTableInfo = isCancel => {
        if (isCancel) {
            this.props.onSelectTableInfo({isCancel: isCancel});
        } else {
            this.props.onSelectTableInfo({
                isCancel: isCancel,
                capacity: 1,
                length: 1,
                width: 1,
            });
        }
    }

    onClickListenerCancel = () => {
        this.handleTableInfo(true);
    }

    onClickListenerOK = () => {
        this.handleTableInfo(false);
    }

    render() {
        return (
            <div className={"tables"}>
                <div>
                    <h2>Add Table</h2>
                </div>
                <div>
                    <p>Table #: </p>
                </div>
                <div className={"input-div"}>
                    <p className={"single-element"}>Seat Capacity: </p>
                    <input type="text" id="capacity" name="fname" className={"text-line"}/>
                </div>
                <div className={"input-div"}>
                    <p className={"single-element"}>Table Size: </p>
                    <p>Length: </p>
                    <input type="text" id="length" name="fname" className={"text-line"}/>
                    <p>Width: </p>
                    <input type="text" id="width" name="fname" className={"text-line"}/>
                </div>
                <div className={"confirmation-bar"}>
                    <button onClick={this.onClickListenerCancel} className={"button-cancel"}>Cancel</button>
                    <button onClick={this.onClickListenerOK} className={"button-ok"}>OK</button>
                </div>
            </div>
        );
    }
}

TableChart.propTypes = {};

export default TableChart;
