import React, {PureComponent} from 'react';
import PropTypes from 'prop-types';
import Tab from "./Tab"
import TableChart from "./TableChart"
// import PopUp from "./PopUp"

class Main extends PureComponent {
    state = {
        chartType: 'tables',
        addNew: false,
        capacity: 0,
        length: 0,
        width: 0,
    }

    addATable = () => {
        this.setState({
            chartType: 'addNew',
            addNew: true,
        });
    }

    handleTableInfo = info => {
        if (info.isCancel) {
            this.setState({
                chartType: 'tables',
                addNew: false,
            });
        } else {
            this.setState({
                chartType: 'tables',
                addNew: false,
                capacity: info.capacity,
                length: info.length,
                width: info.width,
            })
        }
    }

    render() {
        return (
            <div className={"main-container"}>
                <div className={"tabs"}>
                    <Tab />
                </div>
                <div>
                    <button onClick={this.addATable}
                            className={"button"}>
                        Add Table
                    </button>
                </div>

                {
                    !this.state.addNew ?
                        <div className={"tables"}>
                            <h1>Table info:
                                {this.state.capacity}
                                {this.state.length}
                                {this.state.width} 
                            </h1>
                        </div>
                    :
                        <TableChart onSelectTableInfo={this.handleTableInfo}/>
                }
            </div>
        );
    }
}

Main.propTypes = {};

export default Main;
