import React, {Component} from 'react';
import PropTypes from 'prop-types';

class RenderTables extends Component {
    state = {
        tables: this.props.tables,
    }

    checkTable = (tableId) => {
        this.props.onCheckTableInfo({id: tableId});
    }

    onClickListener = (event) => {
        const id = event.target.id;
        this.checkTable(id);
    }

    render() {
        const small_tables = (length, width, capacity) => ({
            float: `left`,
            "font-family": `Arial, serif`,
            border: `silver thin solid`,
            "border-radius":  `10px`,
            height: `80px`,
            width: `80px`,
            margin: `20px`,
            "line-height": `80px`,
            "text-align": `center`,
        });
        return (
            <div className={"tables"}>
                <div >
                    {
                        this.state.tables.map((tables, index) => {
                            return (
                                <div key={index} onClick={this.onClickListener}
                                     style={small_tables(tables.length, tables.width, tables.capacity)}>
                                    A{tables.length} *4
                                </div>
                            );
                        })
                    }
                </div>
            </div>

        );
    }
}

export default RenderTables;

