import React, {PureComponent} from 'react';
import PropTypes from 'prop-types';
import Tab from "./Tab"
// import TableChart from "./TableChart"
// import AddTables from "./AddTables"

class Main extends PureComponent {
    state = {
        numChildren: 0,
    }

    addChildren = () => {
        this.setState({numChildren: this.state.numChildren + 1});
    }

    render() {
        return (
            <div className={"main-container"}>
                <div className={"tabs"}>
                    <Tab />
                </div>
                <div>
                    <button onClick={"addChildren"} className={"button"}>Add Table</button>
                </div>
                <div className={"tables"}>
                    <h1>Child number: `this.state.numChildren`</h1>
                </div>
            </div>
        );
    }
}

Main.propTypes = {};

export default Main;
