import React, {PureComponent} from 'react';
import PropTypes from 'prop-types';
import Tab from "./Tab"
import TableChart from "./TableChart"
// import PopUp from "./PopUp"

class Main extends PureComponent {
    state = {
        chartType: 'tables'
    }

    addATable = () => {
        this.setState({chartType: 'addTable'});
    }

    render() {
        return (
            <div className={"main-container"}>
                <div className={"tabs"}>
                    <Tab />
                </div>
                <div>
                    <button onClick={this.addATable} className={"button"}>Add Table</button>
                </div>

                {
                    this.state.chartType === 'tables' ?
                        <div className={"tables"}>
                            <h1>Child number: {this.state.chartType}</h1>
                        </div>
                    :
                        <TableChart />
                }
            </div>
        );
    }
}

Main.propTypes = {};

export default Main;
