import React, {PureComponent} from 'react';
import PropTypes from 'prop-types';
import Tab from "./Tab"
import TableChart from "./TableChart"
// import PopUp from "./PopUp"

class Main extends PureComponent {
    state = {
        chartType: 'tables',
        numExistingTables: 0,
        tables: [],
    }

    addATable = () => {
        this.setState({
            chartType: 'addNew',
        });
    }

    handleTableInfo = info => {
        if (info.isCancel) {
            this.setState({
                chartType: 'tables',
            });
        } else {
            this.setState({
                chartType: 'tables',
                numExistingTables: 0,
                tables: [
                    this.state.tables,
                    {
                        capacity: info.capacity,
                        length: info.length,
                        width: info.width,
                    }
                ],
            })
        }
    }

    render() {
        if (this.state.chartType === 'tables') {
            const theTables = this.state.tables;
            console.log(theTables);
            if (theTables.length > 0)
                console.log(theTables.length);
            return (
                <div className={"main-container"}>
                    <div className={"tabs"}>
                        <Tab/>
                    </div>
                    <div>
                        <button onClick={this.addATable}
                                className={"button"}>
                            Add Table
                        </button>
                    </div>

                    <div className={"tables"}>
                        {/*{theTables.map(item => (*/}
                        {/*    // eslint-disable-next-line no-unused-expressions*/}
                        {/*    <h3 className={"table1t1"}>*/}
                        {/*        {item.capacity}*/}
                        {/*    </h3>*/}
                        {/*))}*/}
                    </div>
                </div>
            );
        } else {
             return (
                 <div className={"main-container"}>
                     <div className={"tabs"}>
                         <Tab/>
                     </div>
                     <div>
                         <button onClick={this.addATable}
                                 className={"button"}>
                             Add Table
                         </button>
                     </div>

                     <div>
                         <TableChart onSelectTableInfo={this.handleTableInfo}/>
                     </div>
                 </div>

                 );
        }
    }
}

Main.propTypes = {};

export default Main;
