import React, {Component} from 'react';
import AddTable from "./AddTable"
import RenderTables from "./RenderTables"
import TableDetail from "./TableDetail"

class Main extends Component {
    state = {
        chartType: 'tables',
        tables: [],
        curTableId: -1,
    }

    addATable = () => {
        this.setState({
            chartType: 'addNew',
        });
    }

    checkTable = (info) => {
        console.log(info.id);
        this.setState({
           chartType: 'tableDetail',
            curTableId: info.id,
        });
    }

    handleTableInfo = info => {
        if (info.isCancel) {
            this.setState({
                chartType: 'tables',
            });
        } else if(!info.tableId) {
            let prevTables = this.state.tables;
            prevTables.push({
                capacity: info.capacity,
                length: info.length,
                width: info.width,
            }, );
            this.setState({
                chartType: 'tables',
                tables: prevTables,
            });
            console.log(this.state.tables);
        } else {
            let prevTables = this.state.tables;
            this.setState({
                chartType: 'addNew',
            });
        }
    }

    render() {
        let theView;
        if (this.state.chartType === 'tables') {
            theView = <RenderTables onCheckTableInfo={this.checkTable}
                        tablesToRender={this.state.tables}/>;
        } else if (this.state.chartType === 'addNew') {
            theView =  <AddTable onSelectTableInfo={this.handleTableInfo}/>;
        } else if (this.state.chartType === 'tableDetail') {
            let curTableId = this.state.tables.curTableId;
            theView = curTableId === -1 ? <div/>
                : <TableDetail
                    theTable={this.state.tables[curTableId]}
                    theTableId={this.state.curTableId}
                    onSelectTableInfo={this.handleTableInfo}/>;
        }
        return (
            <div className={"main-container"}>

                <div className={"tabs"}>
                    <p className={"tab"}>Dine in</p>
                </div>

                <div>
                    <button onClick={this.addATable}
                            className={"button"}>
                        Add Table
                    </button>
                </div>

                {theView}

            </div>
        );

    }
}

export default Main;
