import React, {PureComponent} from 'react';
import PropTypes from 'prop-types';
import Tab from "./Tab"
import TableChart from "./TableChart"
// import PopUp from "./PopUp"

class Main extends PureComponent {
    state = {
        chartType: 'tables',
        numExistingTables: 0,
        tables: [],
    }

    addATable = () => {
        this.setState({
            chartType: 'addNew',
        });
    }

    handleTableInfo = info => {
        if (info.isCancel) {
            this.setState({
                chartType: 'tables',
            });
        } else {
            this.setState({
                chartType: 'tables',
                numExistingTables: 0,
                tables: this.state.tables.length === 0 ? [
                    {
                        capacity: info.capacity,
                        length: info.length,
                        width: info.width,
                    }
                ] : [
                    this.state.tables,
                    {
                        capacity: info.capacity,
                        length: info.length,
                        width: info.width,
                    }
                ],
            });
            console.log(this.state.tables)
        }
    }

    buildTag = () => {
        const theTables = this.state.tables;
        return theTables.map(item => (
            <h4 className={"table1t1"} >
                A table.
                Capacity: {item.capacity};
                Length: {item.length};
                Width: {item.width}
            </h4>
        ));
    }

    render() {
        if (this.state.chartType === 'tables') {
            const allElements = this.buildTag();

            // console.log(theTables);
            // if (theTables.length > 0)
            //     console.log(theTables[1].length);
            return (
                <div className={"main-container"}>
                    <div className={"tabs"}>
                        <Tab/>
                    </div>
                    <div>
                        <button onClick={this.addATable}
                                className={"button"}>
                            Add Table
                        </button>
                    </div>

                    <div className={"tables"}>
                        {allElements}
                    </div>
                </div>
            );
        } else {
             return (
                 <div className={"main-container"}>
                     <div className={"tabs"}>
                         <Tab/>
                     </div>
                     <div>
                         <button onClick={this.addATable}
                                 className={"button"}>
                             Add Table
                         </button>
                     </div>

                     <div className={"tables"}>
                         <TableChart onSelectTableInfo={this.handleTableInfo}/>
                     </div>
                 </div>

                 );
        }
    }
}

Main.propTypes = {};

export default Main;
