import React, {Component} from 'react';
import PropTypes from 'prop-types';

class AddTable extends Component {

    handleTableInfo = isCancel => {
        if (isCancel) {
            this.props.onSelectTableInfo({isCancel: isCancel});
        } else {
            this.props.onSelectTableInfo({
                isCancel: isCancel,
                capacity: document.getElementById("capacity").value,
                length: document.getElementById("length").value,
                width: document.getElementById("width").value,
            });
        }
    }

    onClickListenerCancel = () => {
        this.handleTableInfo(true);
    }

    onClickListenerOK = () => {
        this.handleTableInfo(false);
    }

    render() {
        return (
            <div className={"tables"}>
                <div className={"sub-title"}>
                    <h2 className={"text-line"}>Add Table</h2>
                </div>
                <div className={"table-info"}>
                    <div className={'text-line'}>
                        <p >Table #: </p>
                    </div>

                    <div className={'text-line'}>
                        <p className={"static-text"}>Seat Capacity: </p>
                        <input className={"input-box"} type="text" id="capacity" name="fname" />
                    </div>

                    <div className={'text-line'}>
                        <p >Table Size: </p>
                        <p className={"static-text"}>Length: </p>
                        <input className={"input-box"} type="text" id="length" name="fname" />
                        <p className={"static-text"}>Width: </p>
                        <input className={"input-box"} type="text" id="width" name="fname" />
                    </div>
                </div>

                <div className={"confirmation-bar"}>
                    <button onClick={this.onClickListenerCancel} className={"button-cancel"}>Cancel</button>
                    <button onClick={this.onClickListenerOK} className={"button-ok"}>OK</button>
                </div>
            </div>
        );
    }
}

AddTable.propTypes = {};

export default AddTable;
