import React, {PureComponent} from 'react';
import PropTypes from 'prop-types';
import Tab from "./Tab"
import TableChart from "./TableChart"
// import PopUp from "./PopUp"

class Main extends PureComponent {
    state = {
        chartType: 'tables',
        numExistingTables: 0,
        tables: {},
    }

    addATable = () => {
        this.setState({
            chartType: 'addNew',
        });
    }

    handleTableInfo = info => {
        if (info.isCancel) {
            this.setState({
                chartType: 'tables',
            });
        } else {
            this.setState({
                chartType: 'tables',
                numExistingTables: 0,
                tables: {
                    ...
                    {
                        capacity: info.capacity,
                        length: info.length,
                        width: info.width,
                    }
                },
            })
        }
    }

    render() {
        return (
            <div className={"main-container"}>
                <div className={"tabs"}>
                    <Tab />
                </div>
                <div>
                    <button onClick={this.addATable}
                            className={"button"}>
                        Add Table
                    </button>
                </div>

                {
                    !this.state.chartType === 'tables' ?
                        <div className={"tables"}>
                            {
                                this.state.tables.forEach(item => {
                                    let x = item.capacity
                                    console.log(x);
                                })
                            }
                        </div>
                    :
                        <TableChart onSelectTableInfo={this.handleTableInfo}/>
                }
            </div>
        );
    }
}

Main.propTypes = {};

export default Main;
