import React, {Component} from 'react';
import PropTypes from 'prop-types';

class RenderTables extends Component {
    state = {
        tables: this.props.tables,
    }

    checkTable = (tableId) => {
        this.props.onCheckTableInfo({id: tableId});
    }

    onClickListener = (event) => {
        const id = event.target.key;
        this.checkTable(id);
    }

    render() {
        console.log(this.state.tables);
        const customizedTables = (length, width) => ({
            float: `left`,
            fontFamily: `Arial, serif`,
            border: `silver thin solid`,
            borderRadius:  `10px`,
            height: `${width * 80}px`,
            width: `${length * 80}px`,
            margin: `20px`,
            lineHeight: `80px`,
            textAlign: `center`,
        });
        let aTable = (length, width, index) => {
            // console.log(length, width, index);
               return <div key={index} onClick={this.onClickListener}
                     style={customizedTables(length, width)}>
                    A{index} *4
                </div>;
        };
        return (
            <div className={"tables"}>
                <div >
                    {
                        this.props.tables.map((tables, index) => {
                            return (
                                aTable(tables.length, tables.width, index)
                            );
                        })
                    }
                </div>
            </div>

        );
    }
}

export default RenderTables;

