import React, {PureComponent} from 'react';


class TableDetail extends PureComponent {

    handleTableInfo = () => {

        this.props.onSelectTableInfo({isCancel: true});

    }

    onClickListenerCancel = () => {
        this.handleTableInfo();
    }

    render() {
        return (
            <div className={"tables"}>
                <div className={"sub-title"}>
                    <h3 className={"text-line"}>Table: A{this.props.theTableId}</h3>
                </div>
                <div className={"detail-container"}>
                    <div className={"text-detail"}>
                        <h4>Order Detail: </h4>
                        <div className={"order-detail"}>
                            <p>Pork Feet Noodles</p>
                            <p>Twice Cooked Pork blablablabla</p>
                            <p>Fish in Chili Sauce</p>
                        </div>
                        <div className={"order-detail"}>
                            <h4>Subtotal: </h4>
                            <p>Discount: </p>
                            <p>Tax: </p>
                            <p>Order Total: </p>
                        </div>
                    </div>
                    <div className={"settings"}>
                        <p className={"settings-item"}>Print Order</p>
                        <p className={"settings-item"}>Print Invoice</p>
                        <p className={"settings-item"}>Add Item</p>
                        <p className={"settings-item"}>End Order</p>
                        <p className={"settings-item"}>Switch Table</p>
                        <p className={"settings-item"}>Table Setting</p>
                    </div>
                </div>


                <div className={"confirmation-bar"}>
                    <button onClick={this.onClickListenerCancel} className={"button-cancel"}>Cancel</button>
                    <button onClick={this.onClickListenerCancel} className={"button-ok"}>OK</button>
                </div>
            </div>
        );
    }
}

export default TableDetail;
