import React, {PureComponent} from 'react';
import PropTypes from 'prop-types';
import Tab from "./Tab"
import TableChart from "./TableChart"
// import PopUp from "./PopUp"

class Main extends PureComponent {
    state = {
        chartType: 'tables',
        numExistingTables: 0,
        tables: {},
    }

    addATable = () => {
        this.setState({
            chartType: 'addNew',
        });
    }

    handleTableInfo = info => {
        if (info.isCancel) {
            this.setState({
                chartType: 'tables',
            });
        } else {
            this.setState({
                chartType: 'tables',
                numExistingTables: 0,
                tables: {
                    ...
                    {
                        capacity: info.capacity,
                        length: info.length,
                        width: info.width,
                    }
                },
            })
        }
    }

    render() {
        if (this.state.chartType === 'tables') {
            return (
                <div className={"main-container"}>
                    <div className={"tabs"}>
                        <Tab/>
                    </div>
                    <div>
                        <button onClick={this.addATable}
                                className={"button"}>
                            Add Table
                        </button>
                    </div>

                    <div className={"tables"}>
                        {
                            this.state.tables.map(item => {
                                // eslint-disable-next-line no-unused-expressions
                                <li className={"table1t1"}>
                                    <p>{item.capacity}</p>
                                </li>
                            })
                        }
                    </div>
                </div>
            );
        }
         else {
             return (
                 <div>
                     <TableChart onSelectTableInfo={this.handleTableInfo}/>
                 </div>
                 );
        }
    }
}

Main.propTypes = {};

export default Main;
