import React, {PureComponent} from 'react';
import PropTypes from 'prop-types';

class TableChart extends PureComponent {
    state = {
        chartType: 'tables',
        addNew: false,
        capcity: 0,
        length: 0,
        width: 0,
    }

    handleTableInfo = isCancel => {
        if (isCancel) return null;
        return {
            capacity: 1,
            length: 1,
            width: 1,
        }
    }

    onClickListenerCancel = () => {
        this.handleTableInfo(true);
    }

    onClickListenerOK = () => {
        this.handleTableInfo(false);
    }

    render() {
        return (
            <div className={"tables"}>
                <div>
                    <h1>Add Table</h1>
                </div>
                <div>
                    <h2>Table #: </h2>
                </div>
                <div>
                    <h2>Seat Capacity: </h2>
                </div>
                <div>
                    <h2>Table Size: </h2>
                </div>
                <div>
                    <button onClick={this.handleTableInfo} className={"button"}>Cancel</button>
                    <button onClick={this.handleTableInfo} className={"button"}>OK</button>
                </div>
            </div>
        );
    }
}

TableChart.propTypes = {};

export default TableChart;
