import React, {Component} from 'react';
import PropTypes from 'prop-types';
import Tab from "./Tab"
import AddTable from "./AddTable"
import RenderTables from "./RenderTables"
// import PopUp from "./PopUp"

class Main extends Component {
    state = {
        chartType: 'tables',
        numExistingTables: 0,
        tables: [],
    }

    addATable = () => {
        this.setState({
            chartType: 'addNew',
        });
    }

    handleTableInfo = info => {
        if (info.isCancel) {
            this.setState({
                chartType: 'tables',
            });
        } else {
            this.setState({
                chartType: 'tables',
                numExistingTables: 0,
                tables: this.state.tables.length === 0 ? [
                    {
                        capacity: info.capacity,
                        length: info.length,
                        width: info.width,
                    }
                ] : [
                    this.state.tables,
                    {
                        capacity: info.capacity,
                        length: info.length,
                        width: info.width,
                    }
                ],
            });
            // console.log(this.state.tables);
        }
    }

    render() {
            return (
                <div className={"main-container"}>
                    <div className={"tabs"}>
                        <Tab/>
                    </div>
                    <div>
                        <button onClick={this.addATable}
                                className={"button"}>
                            Add Table
                        </button>
                    </div>
                    {   this.state.chartType === 'tables' ?
                            <RenderTables />
                        :

                        <AddTable onSelectTableInfo={this.handleTableInfo}/>

                    }
                </div>
            );

    }
}

Main.propTypes = {};

export default Main;
