import React, {PureComponent} from 'react';
import PropTypes from 'prop-types';
import Tab from "./Tab"
import TableChart from "./TableChart"
// import PopUp from "./PopUp"

class Main extends PureComponent {
    state = {
        chartType: 'tables',
        numExistingTables: 0,
        tables: [],
    }

    addATable = () => {
        this.setState({
            chartType: 'addNew',
        });
    }

    handleTableInfo = info => {
        if (info.isCancel) {
            this.setState({
                chartType: 'tables',
            });
        } else {
            this.setState({
                chartType: 'tables',
                numExistingTables: 0,
                tables: this.state.tables.length === 0 ? [
                    {
                        capacity: info.capacity,
                        length: info.length,
                        width: info.width,
                    }
                ] : [
                    this.state.tables,
                    {
                        capacity: info.capacity,
                        length: info.length,
                        width: info.width,
                    }
                ],
            });
            // console.log(this.state.tables);
        }
    }

    render() {
        if (this.state.chartType === 'tables') {
            const data = this.state.tables;
            const d = data[0];
            console.log(d);
            // const allElements = data.map((d) => (
            //     <h4 className={"table1t1"}
            //         key={d.capacity * d.capacity * d.capacity
            //             + d.length * d.length + d.width}>
            //         A table.
            //         Capacity: {d.capacity};
            //         Length: {d.length};
            //         Width: {d.width}.
            //     </h4>
            // ));
            // console.log(theTables);
            // if (theTables.length > 0)
            //     console.log(theTables[1].length);
            return (
                <div className={"main-container"}>
                    <div className={"tabs"}>
                        <Tab/>
                    </div>
                    <div>
                        <button onClick={this.addATable}
                                className={"button"}>
                            Add Table
                        </button>
                    </div>

                    <div className={"tables"}>
                        {/*{allElements}*/}
                        {
                                <h4 className={"table1t1"}
                                    key={d.capacity * d.capacity * d.capacity
                                    + d.length * d.length + d.width}>
                                    A table.
                                    Capacity: {d.capacity};
                                    Length: {d.length};
                                    Width: {d.width}.
                                </h4>
                        }
                    </div>
                </div>
            );
        } else {
             return (
                 <div className={"main-container"}>
                     <div className={"tabs"}>
                         <Tab/>
                     </div>
                     <div>
                         <button onClick={this.addATable}
                                 className={"button"}>
                             Add Table
                         </button>
                     </div>

                     <div className={"tables"}>
                         <TableChart onSelectTableInfo={this.handleTableInfo}/>
                     </div>
                 </div>

                 );
        }
    }
}

Main.propTypes = {};

export default Main;
